import React from 'react'

const Projects = () => {
    const projectList = [
        {
            title: "E-commerce Website",
            description: "A full-featured MERN stack e-commerce app with user auth, payment gateway, and admin dashboard.",
            link: "https://your-ecommerce-app.com"
        },
        {
            title: "Chat App",
            description: "Real-time chat application using Socket.io and Node.js with group chat and emojis.",
            link: "https://your-chat-app.com"
        },
        {
            title: "Portfolio Website",
            description: "Responsive portfolio built with React showcasing my work and contact information.",
            link: "https://your-portfolio.com"
        }
    ];

    return (
        <div className="p-6 md:p-12 bg-gray-100 min-h-screen">
            <h2 className="text-3xl font-bold mb-8 text-center">My Projects</h2>
            <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
                {projectList.map((project, index) => (
                    <div
                        key={index}
                        className="bg-white rounded-2xl shadow-md p-6 hover:shadow-xl transition"
                    >
                        <h3 className="text-xl font-semibold mb-2">{project.title}</h3>
                        <p className="text-gray-700 mb-4">{project.description}</p>
                        <a
                            href={project.link}
                            target="_blank"
                            rel="noopener noreferrer"
                            className="text-blue-600 hover:underline"
                        >
                            Visit Project â†’
                        </a>
                    </div>
                ))}
            </div>
        </div>
    );
};




export default Projects
