import React from 'react';
import image from '../../image/image.png';
import './home.css';

import Contact from '../Contact/Contact';
import About from '../About/About';
import Projects from '../Projects/Projects';

const Home = () => {
    return (
        <div className="home-container">
            {/* Profile Image and Hover Effects */}
            <div className="image-section">
                <div className="image-wrapper">
                    <img src={image} alt="Vishal Kumar" className="profile-image" />
                    <span className="name left">Vishal</span>
                    <span className="name right">Kumar</span>

                    {/* Animated Roles on Hover */}
                    <div className="role left-role">üíª Full Stack Developer</div>
                    <div className="role right-role">‚öõÔ∏è MERN Stack Developer</div>
                    <div className="role left-role">üåê Web Developer</div>
                    <div className="role right-role">üéÆ Game Developer</div>
                </div>
            </div>

            {/* About + Contact Side by Side */}
            <div className="text-section">
                <div className="about-wrapper">
                    <About />
                </div>
                <div className="contact-wrapper">
                    <Contact />
                </div>
            </div>

            {/* Projects Section */}
            <div className="projects-wrapper">
                <Projects />
            </div>
        </div>
    );
};

export default Home;
